import React, { useState, useEffect, useReducer } from 'react';
import Registration_First from './_Registration_First';


export const RegistrationContext = React.createContext()


const initialState = {
    name: '',
    age: '',
}

const reducer = (prevState, action) => {
    switch (action.type) {
        case "registration_first":
            return {
                ...prevState,
                [action.field]: action.value
            }

        case "registration_second":
            return {
                ...prevState,
                [action.field]: action.value
            }
        default:
            return prevState
    }
}
const RegistrationComponent = () => {
    const [state, dispatch] = useReducer(reducer, initialState)


    const onChange = e => {
        dispatch({ type: "registration_first", field: e.target.name, value: e.target.value })
    }

    const { name, age } = state;

    return (
        <React.Fragment>
            
            {state.name} {state.age}

            <RegistrationContext.Provider value={{ Regis_State: state, Regis_Dispatch: dispatch }}>
                <Registration_First />
            </RegistrationContext.Provider>
        </React.Fragment>

    )

}


export default RegistrationComponent;
