import React from 'react'
import { useForm, ErrorMessage } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { useStateMachine } from "little-state-machine";
import __updateAction from './__updateAction';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List
} from 'semantic-ui-react';


const __RegistrationForm2 = () => {

    const { state, action } = useStateMachine(__updateAction);

    const { handleSubmit, errors, register } = useForm({
        defaultValues: state.yourDetails
    });

    const { push } = useHistory();

    const onSubmit = (data) => {
        action(data);
        push("/__Result");
        
    };


    return (
        <Grid textAlign='center' verticalAlign='middle'>
            <Grid.Column style={{ maxWidth: 450 }}>
                <Header as='h2' color='black' textAlign='center' style={{ padding: "10px" }}>
                    Registration
                </Header>
                <br />
                <Divider horizontal>Or</Divider>

                <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                    <Segment stacked>
                        <Form.Field>
                            <label>First Name</label>

                            <input
                                fluid
                                icon='user'
                                iconPosition='left'
                                placeholder='E-mail address'
                                name="age"
                                type="number"
                                ref={register({
                                    required: "This is required",
                                    min: {
                                        value: 18,
                                        message: "You are required to be 18 above."
                                    }
                                })}
                            />
                        </Form.Field>
                        {errors.age && <p>Username invalid</p>}

                        <Button
                            color='teal'
                            fluid
                            size='large'
                            type="submit"
                        >
                            Next
                        </Button>
                    </Segment>
                </Form>
            </Grid.Column>
        </Grid>
    )
}

export default __RegistrationForm2;
