import React from 'react'
import { useForm, Error } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { useStateMachine } from "little-state-machine";
import __updateAction from '../RegistrationComponents/__updateAction';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List
} from 'semantic-ui-react';


const __RegistrationForm1 = () => {

    const { state, action } = useStateMachine(__updateAction);

    const { handleSubmit, register, errors } = useForm({
        defaultValues: state.yourDetails
    });

    const { push } = useHistory();

    const onSubmit = (data) => {
        action(data);
        push("/__RegistrationForm2");
    };


    return (
        <Grid textAlign='center' verticalAlign='middle'>
            <Grid.Column>
                <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                    <Segment stacked>
                        <Form.Field>
                            <label>First Name</label>

                            <input
                                fluid
                                icon='user'
                                iconPosition='left'
                                placeholder='Enter your details'
                                type='text'
                                name="firstName"
                                ref={register({ validate: firstName => firstName && firstName.length > 3 })}
                            />
                        </Form.Field>
                        {errors.firstName && <p>Username invalid</p>}

                        <Button
                            color='teal'
                            fluid
                            size='large'
                            type="submit"
                        >
                            Next
                        </Button>
                    </Segment>
                </Form>
                <Form>
                    <Form.Group widths='equal'>
                        <Form.Field
                            id='form-input-control-first-name'
                            control={Input}
                            label='First name'
                            placeholder='First name'
                        />
                        <Form.Field
                            id='form-input-control-last-name'
                            control={Input}
                            label='Last name'
                            placeholder='Last name'
                        />
                        <Form.Field
                            control={Select}
                            options={genderOptions}
                            label={{ children: 'Gender', htmlFor: 'form-select-control-gender' }}
                            placeholder='Gender'
                            search
                            searchInput={{ id: 'form-select-control-gender' }}
                        />
                    </Form.Group>
                    <Form.Field
                        id='form-textarea-control-opinion'
                        control={TextArea}
                        label='Opinion'
                        placeholder='Opinion'
                    />
                    <Form.Field
                        id='form-input-control-error-email'
                        control={Input}
                        label='Email'
                        placeholder='joe@schmoe.com'
                        error={{
                            content: 'Please enter a valid email address',
                            pointing: 'below',
                        }}
                    />
                    <Form.Field
                        id='form-button-control-public'
                        control={Button}
                        content='Confirm'
                        label='Label with htmlFor'
                    />
                </Form>
               
            </Grid.Column>
        </Grid>
    )
}

export default __RegistrationForm1;
